package com.primos.core.logger;

import com.primos.core.constants.GBComStrConstants;
import java.util.Date;
import org.apache.log4j.PatternLayout;

/**
 * This class contains the log header for aplication.
 * @author GAOQ
 * @version 1.0
 * @since 28/09/2018
 */
public class PRPatterLayout extends PatternLayout{
    
    /**
     * This method return the log header to write.
     * @return String the log header.
     */
    @Override
    public String getHeader(){
        return  "* *****************************************************************************************************************************************************\n" +
                "* *****************************************************************************************************************************************************\n" +
                "* ***************************** ************ ********** ************ ******     ****** **************** **************** ******************************\n" +
                "* ***************************** ************ ********** ************  ******   ******  **************** **************** ******************************\n" +
                "* ***************************** **      **** **    ****      **       ******* ***  **  ****        **** ****             ******************************\n" +
                "* ***************************** **      **** **    ****      **       **   ******  **  ****        **** ****             ******************************\n" +
                "* ***************************** **      **** **    ****      **       **   **  **  **  ****        **** ****             ******************************\n" +
                "* ***************************** ************ **********      **       **   **  **  **  ****        **** **************** ******************************\n" +
                "* ***************************** ************ **********      **       **   **  **  **  ****        **** **************** ******************************\n" +
                "* ***************************** **           ****            **       **   **  **  **  ****        ****             **** ******************************\n" +
                "* ***************************** **           ** **           **       **   **  **  **  ****        ****             **** ******************************\n" +
                "* ***************************** **           **  **          **       **   **  **  **  ****        ****             **** ******************************\n" +
                "* ***************************** **           **   **         **       **   **  **  **  ****        ****             **** ******************************\n" +
                "* ***************************** **           **    **   ************  **   **  **  **  **************** **************** ******************************\n" +
                "* ***************************** **           **     **  ************  **   **  **  **  **************** **************** ******************************\n" +
                "* *****************************************************************************************************************************************************\n" +
                "* *****************************************************************************************************************************************************\n" +
                "* *****************************************************************************************************************************************************\n" +
                "*******************************************************************************************************************************************************\n" +
                    "\tEnviroment execution properties for "+GBComStrConstants.APP_NAME+"\n" +
                    "\tServer: "+System.getProperty("os.name")+" Version: "+System.getProperty("os.version")+"\n" +
                    "\tArch: "+System.getProperty("os.arch")+"\n" +
                    "\tJava version: "+System.getProperty("java.version")+"\n" +
                    "\tJava home: "+System.getProperty("java.home")+"\n" +
                    "\tInstalation path: "+System.getProperty("user.dir")+"\n" +
                "*******************************************************************************************************************************************************\n" +
                    "\tStart the log event register, the start date: "+new Date().toString()+"\n" +
                "*******************************************************************************************************************************************************\n";
    }
}
